    <script>
        document.addEventListener('livewire:init', function () {

            let disabledElements = document.querySelectorAll('.enable-after-load');
            
            // Loop through the selected elements and remove the 'disabled' attribute
            disabledElements.forEach(function (element) {
                element.removeAttribute('disabled'); // Enable the input fields
                element.classList.remove('enable-after-load'); 
            });
        });
    </script>

